    }

    function handleCusChange(event, customer) {
        const { name, value } = event.target;
        customers.forEach((cus) => {
            if (cus.ID === customer.ID) {
                cus[name] = value;
            }
        });
        setCustomers([...customers]);
    }

    function handleBusChange(event, business) {
        const { name, value } = event.target;
        businesss.forEach((cus) => {
