    }

    function addOrEditBusiness(business) {
        axios({
            method: 'post',
            url: 'http://localhost/wdm_phase4/React/src/api' + '/business.php',
            headers: {
                'content-type': 'application/json'
            },
            data: { Function: (business.editbusiness ? 'alterRecordb' : 'addNewBusiness'), Data: business }
